function initializeCarousel(){var t=document.querySelector("#carouselExampleIndicators");t&&new bootstrap.Carousel(t,{interval:3e3,wrap:!0})}function startCountdown(t,e){let a=document.getElementById(t);if(a){let n=e,r=setInterval(()=>{var t,e,o;n<=0?(clearInterval(r),a.textContent="¡Oferta expirada!",a.classList.remove("text-danger"),a.classList.add("text-muted")):(t=Math.floor(n/3600),e=Math.floor(n%3600/60),o=n%60,a.textContent=`¡Oferta termina en ${t}h ${e}m ${o}s!`,n--)},1e3)}else console.error(`Elemento con id ${t} no encontrado`)}function setupSearchExpansion(){let t=document.querySelector(".form-control");t&&(t.addEventListener("focus",()=>{t.style.width="300px"}),t.addEventListener("blur",()=>{t.style.width="250px"}))}function setupSmoothScroll(){document.querySelectorAll('a[href^="#"]').forEach(t=>{t.addEventListener("click",function(t){t.preventDefault(),document.querySelector(this.getAttribute("href")).scrollIntoView({behavior:"smooth"})})})}document.addEventListener("DOMContentLoaded",()=>{initializeCarousel(),setupSearchExpansion(),startCountdown("offerCountdown1",10800),startCountdown("offerCountdown2",14400),startCountdown("offerCountdown3",18e3),startCountdown("offerCountdown4",21600),setupSmoothScroll()});